<?phpsession_start();require_once 'config.php';$metode = req_handler('m');$uname = req_handler('uname');//AL : Show SIM List//AD : Show SIM Detail//BL : Show STNK List//BD : Show STNK Detailswitch($metode){	case 'AL' : if (db_num("SELECT * FROM `user_sim` WHERE `username` = '$uname'") == 1)				{					$data_post = db_fetchs("SELECT `sim`.`no_sim`,`sim`.`nama`,`sim`.`golongan` FROM `sim`,`user_sim` WHERE `sim`.`no_sim` = `user_sim`.`no_sim` AND `user_sim`.`username` = '$uname'");					if ($data_post  == 0)					{						echo json_encode(null); // Data Tilang Not Found					}					else					{						for ($i=1;$i<=$data_post[0];$i++)							$dt[$i-1] = $data_post[$i];						echo json_encode($dt);					}				}				else				{					echo json_encode(null); // Username not found				}				break;					case 'AD' : $no_sim = req_handler('no_sim');				if (db_num("SELECT * FROM `user_sim` WHERE `username` = '$uname' AND `no_sim` = '$no_sim'") == 1)				{					$data_post = db_fetchs("SELECT `sim`.* FROM `sim`,`user_sim` WHERE `sim`.`no_sim` = '$no_sim' AND `user_sim`.`username` = '$uname'");					if ($data_post  == 0)					{						echo json_encode(null); // Data Tilang Not Found					}					else					{						for ($i=1;$i<=$data_post[0];$i++)							$dt[$i-1] = $data_post[$i];						echo json_encode($dt);					}				}				else				{					echo json_encode(null); // Username not found				}				break;		case 'BL' : if (db_num("SELECT * FROM `user_stnk` WHERE `username` = '$uname'") == 1)				{					$data_post = db_fetchs("SELECT `stnk`.`no_stnk`,`stnk`.`nama`,`stnk`.`golongan` FROM `stnk`,`user_stnk` WHERE `stnk`.`no_stnk` = `user_stnk`.`no_stnk` AND `user_stnk`.`username` = '$uname'");					if ($data_post  == 0)					{						echo json_encode(null); // Data Tilang Not Found					}					else					{						for ($i=1;$i<=$data_post[0];$i++)							$dt[$i-1] = $data_post[$i];						echo json_encode($dt);					}				}				else				{					echo json_encode(null); // Username not found				}				break;					case 'BD' : $no_stnk = req_handler('no_stnk');				if (db_num("SELECT * FROM `user_stnk` WHERE `username` = '$uname' AND `no_stnk` = '$no_stnk'") == 1)				{					$data_post = db_fetchs("SELECT `stnk`.* FROM `stnk`,`user_stnk` WHERE `stnk`.`no_stnk` = '$no_stnk' AND `user_stnk`.`username` = '$uname'");					if ($data_post  == 0)					{						echo json_encode(null); // Data Tilang Not Found					}					else					{						for ($i=1;$i<=$data_post[0];$i++)							$dt[$i-1] = $data_post[$i];						echo json_encode($dt);					}				}				else				{					echo json_encode(null); // Username not found				}				break;					default :	echo json_encode(null);}?>